import BaseBottomSheet, {
  BottomSheetBackdrop,
  BottomSheetBackdropProps,
  BottomSheetScrollView,
} from '@gorhom/bottom-sheet';
import { useAppTheme } from '@src/theme';
import React from 'react';
import { BottomSheetProps } from './BottomSheet.type';

export const BottomSheet = React.forwardRef<
  React.ElementRef<typeof BaseBottomSheet>,
  React.ComponentPropsWithoutRef<typeof BaseBottomSheet> & BottomSheetProps
>((props, ref) => {
  const { children, useModal, useScrollView = true, ...rest } = props;
  const { colors } = useAppTheme();

  const renderBackdrop = React.useCallback(
    (backdropProps: BottomSheetBackdropProps) => (
      <BottomSheetBackdrop
        {...backdropProps}
        disappearsOnIndex={0}
        appearsOnIndex={1}
      />
    ),
    [],
  );

  const renderContent = () => {
    if (useScrollView) {
      return (
        <BottomSheetScrollView
          style={{
            backgroundColor: colors.background,
          }}
          contentContainerStyle={{
            backgroundColor: colors.background,
          }}>
          <>{children}</>
        </BottomSheetScrollView>
      );
    }

    return <>{children}</>;
  };

  return (
    <BaseBottomSheet
      ref={ref}
      backdropComponent={renderBackdrop}
      handleStyle={{
        backgroundColor: colors.card,
      }}
      handleIndicatorStyle={{
        backgroundColor: colors.secondary,
      }}
      detached={useModal}
      bottomInset={useModal ? 46 : undefined}
      {...rest}>
      {renderContent()}
    </BaseBottomSheet>
  );
});
