import {
  BottomSheet,
  Box,
  Card,
  Carousel,
  CheckBox,
  Text,
} from '@src/components';
import React from 'react';
import { Dimensions, ScrollView } from 'react-native';
import GorhomBottomSheet from '@gorhom/bottom-sheet';
export const Explore = () => {
  const sheetRef = React.useRef<GorhomBottomSheet>(null);
  const width = Dimensions.get('window').width;
  return (
    <Box flex={1}>
      <ScrollView
        contentContainerStyle={{
          paddingVertical: 16,
        }}>
        <Carousel
          loop={false}
          numItemsPerSlide={1.2}
          height={250}
          width={width}
          data={[...new Array(6).keys()]}
          renderItem={() => (
            <Card
              title="Food Star"
              marginHorizontal="s"
              subTitle="Western, Asian, European, American, Italian, French, etc. , American, Italian, French, etc."
              subTitleProps={{
                numberOfLines: 1,
              }}
              coverImage="https://codecanyon.img.customer.envatousercontent.com/files/332335428/food-star-preview-image.jpg?auto=compress%2Cformat&q=80&fit=crop&crop=top&max-h=8000&max-w=590&s=9cd97ec4fa07989227b3ad34cb00ea1e"
              onPress={() => console.log('Card Pressed')}
            />
          )}
        />
        <CheckBox
          label="Check me please"
          onChange={(e) => {
            if (e) {
              sheetRef.current?.snapToIndex(1);
            } else {
              sheetRef.current?.snapToIndex(0);
            }
          }}
          rightElement={<Text>Hello</Text>}
        />
      </ScrollView>
      <BottomSheet ref={sheetRef} snapPoints={['3%', '90%']}>
        <Text>Bottom Sheet</Text>
        <Card
          title="Food Star"
          marginHorizontal="s"
          subTitle="Western, Asian, European, American, Italian, French, etc. , American, Italian, French, etc."
          subTitleProps={{
            numberOfLines: 1,
          }}
          coverImage="https://codecanyon.img.customer.envatousercontent.com/files/332335428/food-star-preview-image.jpg?auto=compress%2Cformat&q=80&fit=crop&crop=top&max-h=8000&max-w=590&s=9cd97ec4fa07989227b3ad34cb00ea1e"
          onPress={() => console.log('Card Pressed')}
        />
      </BottomSheet>
    </Box>
  );
};
