import { createRestyleComponent, createVariant } from '@shopify/restyle';
import { Theme } from '@src/theme';
import { CardProps, CardVariants } from './Card.type';
import { Box } from '../Box';
import { CardContent } from './CardContent';
import { TouchableOpacity } from 'react-native';

const InnerCard = createRestyleComponent<
  CardVariants & React.ComponentProps<typeof Box> & CardProps,
  Theme
>([createVariant({ themeKey: 'cardVariants' })], Box);

export const Card = ({
  title,
  subTitle,
  titleProps,
  subTitleProps,
  coverImage,
  variant,
  onPress,
  coverImageProps,
  coverImageSize,
  children,
  ...rest
}: CardProps) => {
  const renderCardContent = () => {
    return (
      <CardContent
        coverImageSource={coverImage}
        coverImageSize={coverImageSize}
        coverImageProps={coverImageProps}
        title={title}
        subTitle={subTitle}
        titleProps={titleProps}
        subTitleProps={subTitleProps}>
        {children}
      </CardContent>
    );
  };

  return (
    <InnerCard
      backgroundColor="card"
      borderRadius="m"
      variant={variant}
      {...rest}>
      {onPress ? (
        <TouchableOpacity activeOpacity={0.5} onPress={onPress}>
          {renderCardContent()}
        </TouchableOpacity>
      ) : (
        renderCardContent()
      )}
    </InnerCard>
  );
};
