import { Box } from '@src/components/Box';
import { CardCoverImage } from '../CardCoverImage';
import { CardContentProps } from './CardContent.type';
import { Text } from '@src/components/Text';

export const CardContent = ({
  children,
  coverImageProps,
  coverImageSize,
  coverImageSource,
  subTitle,
  title,
  subTitleProps,
  titleProps,
}: CardContentProps) => {
  return (
    <>
      <CardCoverImage
        source={coverImageSource}
        size={coverImageSize}
        {...coverImageProps}
      />
      <Box padding="m">
        <Box>
          {title && (
            <Text fontSize={18} fontWeight="bold" {...titleProps}>
              {title}
            </Text>
          )}
          {subTitle && (
            <Text color="secondary" {...subTitleProps}>
              {subTitle}
            </Text>
          )}
        </Box>
        {children ? <Box marginTop="m">{children}</Box> : null}
      </Box>
    </>
  );
};
